import React from 'react';
import { Router, Route, Link, browserHistory, IndexRoute } from 'react-router';
import ReactDOM from 'react-dom';
import axios from 'axios';
class View extends React.Component {
   constructor(props){
      super(props);
      this.state = {
         userdata : []
      }
      var self = this;
      axios.get('http://localhost:8080/allusers',{responseType: 'arraybuffer',
      headers: {
          'Content-Type': 'application/json',
          'Accept': 'application/pdf'
      }}).then(function(response){
         console.log(response.data)
         self.setState({userdata:response.data},()=>{
            console.log(self.state.userdata)
         })
      })
   }
   
    render() {
       return (
          <div>
             <center><table border ="2">
      <tbody>
         <th>Name</th>
         <th>Bio</th>
         <th>LinkedIn Profile</th>
         <th>Github Profile</th>
         <th>Resume</th>
         {this.state.userdata.map(function(item, key) {
            function downloadURI (url, name) {
               var link = document.createElement('a')
               link.download = name
               link.href = url
               document.body.appendChild(link)
               link.click()
               document.body.removeChild(link)
             }
             function download() {
                
                var blob = new Blob([item.resume.data],{type: "application/pdf"})
                var url = URL.createObjectURL(blob);
                downloadURI(url, item.name +' - resume')
             }
               return (
                  <tr key = {key}>
                      <td>{item.name}</td>
                      <td>{item.bio}</td>
                      <td><a target="_blank" href={item.linkedinlink}><button >
               LinkedIn Profile</button></a></td>
               <td><a target="_blank" href={item.githublink}><button >
               Github Profile</button></a></td>
               <td><button onClick={download}>Download</button></td>
                  </tr>
                )
             
             })}</tbody>
       </table></center>
          </div>
       )
    }
  }
  export default View;
