import React from 'react';
import { Router, Route, Link, browserHistory, IndexRoute } from 'react-router';
import ReactDOM from 'react-dom';
import '../node_modules/bootstrap/dist/css/bootstrap.min.css';
import './App.css';
import FilesUploadComponent from './fileupload';
class Add extends React.Component {
   constructor(props) {
      super(props);
      this.state = {
        name: '',
        bio:'',
        linkedinlink:'',
        githublink:''
      };
    }
    handleChange = (event) => {
      this.setState({[event.target.name]: event.target.value});
    }
    handleSubmit = (event) => {
      alert('A form was submitted: ' + this.state);
  
      fetch('https://your-node-server-here.com/api/endpoint', {
          method: 'POST',
          // We convert the React state to JSON and send it as the POST body
          body: JSON.stringify(this.state)
        }).then(function(response) {
          console.log(response)
          return response.json();
        });
  
      event.preventDefault();
  }
    render() {
       return (
          <div>
             <h1>Add Profile</h1>
             <br />
             <br />
             <form>
                <p>Enter your Name</p>
                <input type="text" name ="name" onChange={this.handleChange}/>
                <p>Enter about yourself</p>
                <textarea name="bio" rows="10" cols="50" onChange={this.handleChange}></textarea>
                <p>Enter your LinkedIn Profile</p>
                <input type="text" name ="linkedinlink" onChange={this.handleChange}/>
                <p>Enter your Github Profile</p>
                <input type="text" name ="githublink" onChange={this.handleChange}/>
             <FilesUploadComponent />
             <input type="submit" value="Upload" />
             </form>

          </div>
       )
    }
  }
  export default Add;
