import React from 'react';
import axios from 'axios';
import { Router, Route, Link, browserHistory, IndexRoute } from 'react-router';
import ReactDOM from 'react-dom';
import '../node_modules/bootstrap/dist/css/bootstrap.min.css';
import './App.css';
import FilesUploadComponent from './fileupload';
class Add extends React.Component {
   constructor(props) {
      super(props);
      this.state = {
        name: '',
        bio:'',
        linkedinlink:'',
        githublink:'',
        selectedFile:''
      };
    }
    handleChange = (event) => {
       var str1 = event.target.name.toString();
       if(str1.localeCompare('selectedFile')==0){
         this.setState({ selectedFile: event.target.files[0] });
       }
       else{
         this.setState({[event.target.name]: event.target.value});
      }
    }
    onSubmit = (e) => {
      e.preventDefault();
      const { name,bio,linkedinlink,githublink,selectedFile } = this.state;
      let formData = new FormData();

      formData.append('name', name);
      formData.append('bio', bio);
      formData.append('linkedinlink',linkedinlink);
      formData.append('githublink',githublink);
      formData.append('selectedFile',selectedFile);
      axios.post('http://localhost:8080/', formData)
        .then((result) => {
          // access results...
        });
    }
    
    render() {
       return (
          <div>
             <h1>Add Profile</h1>
             <br />
             <br />
             <form>
                <p>Enter your Name</p>
                <input type="text" name ="name" onChange={this.handleChange}/>
                <p>Enter about yourself</p>
                <textarea name="bio" rows="10" cols="50" onChange={this.handleChange}></textarea>
                <p>Enter your LinkedIn Profile</p>
                <input type="text" name ="linkedinlink" onChange={this.handleChange}/>
                <p>Enter your Github Profile</p>
                <input type="text" name ="githublink" onChange={this.handleChange}/>
                <input
              type="file"
              name="selectedFile"
              onChange={this.onChange}
            />
             <input type="submit" value="Upload" />
             </form>

          </div>
       )
    }
  }
  export default Add;
