import React from 'react';
import { Router, Route, Link, browserHistory, IndexRoute } from 'react-router';
import ReactDOM from 'react-dom';
import axios from 'axios';
class View extends React.Component {
   constructor(props){
      super(props);
      this.state = {
         userdata : []
      }
      var self = this;
      axios.get('http://localhost:8080/allusers',{mode:'cors'}).then(function(response){
         console.log(response.data)
         self.setState({userdata:response.data},()=>{
            console.log(self.state.userdata)
         })
      })
   }
   
    render() {
       return (
          <div>
             <center><table border ="2">
      <tbody>
         <th>Name</th>
         <th>Bio</th>
         <th>LinkedIn Profile</th>
         <th>Github Profile</th>
         {this.state.userdata.map(function(item, key) {
             
               return (
                  <tr key = {key}>
                      <td>{item.name}</td>
                      <td>{item.bio}</td>
                      <td><a target="_blank" href="https://meetflo.zendesk.com/hc/en-us/articles/230425728-Privacy-Policies"><button >
                        LinkedIn Profile</button></a></td>
                      <td><a href="{item.githublink}">Github</a></td>
                  </tr>
                )
             
             })}</tbody>
       </table></center>
          </div>
       )
    }
  }
  export default View;
